var utilized = ((parseInt(objJSON[0].TotalVisibleMemorySize) - parseInt(objJSON[0].FreePhysicalMemory)) / parseInt(objJSON[0].TotalVisibleMemorySize)) * 100;

var result = parseInt(utilized);


