// DEBUG assetType = "LAPTOP";

var queryC = undefined;
var maxItems = 5000;
// Query
if (customerName == undefined && siteName == undefined && assetName == undefined 
    && assetType == undefined && assetID == undefined && assetID == undefined 
    && assetTag == undefined && assetSerialNumber == undefined) {
    var queryC = {
        "filters": {
            "fieldName": "Customer",
            "type": "NE",
            "value": ""
        }
    };
    maxItems = 1000;
} else {
    var queryC = {
        "filters": {
            "type": "And",
            "filters": [{
                "type": "NE",
                "fieldName": "Customer",
                "value": "TEST"
            }]
        },
        "sorts": [{
            "fieldName": "Customer",
            "isAscending": "false"
        }]
    };    

    if (customerName && customerName != '') {
        queryC.filters.filters.push({
            "fieldName": "Customer",
            "type": "EQ",
            "value": customerName
        });
    }
    
    if (siteName && siteName != '') {
        queryC.filters.filters.push({
            "fieldName": "Asset_StoreIdentifier",
            "type": "LIKE",
            "value": "%" + siteName + "%"
        });
    }    
    
    if (assetName && assetName != '') {
        queryC.filters.filters.push({
            "fieldName": "name",
            "type": "LIKE",
            "value": "%" + assetName + "%"
        });
    }

    if (assetType && assetType != '') {
        queryC.filters.filters.push({
            "fieldName": "Asset_Type",
            "type": "EQ",
            "value": assetType
        });
    }

    if (assetID && assetID != '') {
        queryC.filters.filters.push({
            "fieldName": "Asset_ID",
            "type": "LIKE",
            "value": "%" + assetID + "%"
        });
    }

    if (assetTag && assetTag != '') {
        queryC.filters.filters.push({
            "fieldName": "Asset_Tag",
            "type": "LIKE",
            "value": "%" + assetTag + "%"
        });
    }

    if (assetSerialNumber && assetSerialNumber != '') {
        queryC.filters.filters.push({
            "fieldName": "Asset_Serial_Number",
            "type": "LIKE",
            "value": "%" + assetSerialNumber + "%"
        });
    }
    
}

var params = {
    maxItems: maxItems   /* NUMBER */,
    nameMask: undefined /* STRING */,
    query: queryC /* QUERY */,
    tags: undefined /* TAGS */
};

// result: INFOTABLE dataShape: RootEntityList
