// DEBUG logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper::GetCompanyAssetHierarchy::bdData.rows.length:" + bdData.rows.length);

// Sort results by Customer
var sort = new Object();
sort.name = "Customer";
sort.ascending = false;
