    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Start");
}

var params = {
	maxItems: 10 /* NUMBER */
};

// result: INFOTABLE dataShape: BoundName
var itUnBound = Resources["DeviceFunctions"].GetUnboundRemoteThings(params);


var tableLength = itUnBound.rows.length;
if (enablescriptdebug) {
    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Number of Devices Found:" 
                 + tableLength);
}

/* 
//maxItems not used by Service
if (tableLength > 10) {tableLength = 10;} 
*/

for (var x = 0; x < tableLength; x++) {
    var row = itUnBound.rows[x];

	var bCreateThing = false;

    try {	
        if (enablescriptdebug) { logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Check If Exists:" + row.name); }
        Things[thingName].name;
        if (enablescriptdebug) { logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:FoundThing: " + row.name);}
    }
    catch(e) {         
        if (enablescriptdebug) {
            // logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Thing not Found:" + e.toString());
        }

        var bCreateThing = true;       

    }    

    if (bCreateThing == true) {
        logger.info("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:CreateThing: " + row.name);

        var paramsCT = {
            name: row.name,
            description: "Unbound Registered DCS Thing",
            thingTemplateName: "NCR.LAPTOP",
            tags: "Applications:NCR"
        };

        try {
            Resources["EntityServices"].CreateThing(paramsCT);
        	if (enablescriptdebug) { logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Created:" + row.name); }
        	// Make it available to the platform   
        	Things[row.name].EnableThing();
        	if (enablescriptdebug) { logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:ThingEnabled: " + row.name); }
        	Things[row.name].RestartThing();
        	if (enablescriptdebug) { logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:ThingRestarted: " + row.name); }
            // Set Customer Name
            if (row.name.indexOf("WMT_") == 0) {
                Things[row.name].Customer = "Walmart";
            }            
        	if (enablescriptdebug) { logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Customer Name Set to Walmart"); }
        } catch (err) {
            if (enablescriptdebug) { 
                logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:CreateThingForUnboundThing:Create Failed:" 
                             + row.name + ":ERROR:" + err.toString());
            }
        }
    }
}

