var bDoNotInclAssetTypes = true;

// if certain values of the search are defined do not use siteNameTG and customerNameTG and assetTypeTG
if ((assetName != undefined && assetName != "")
    || (assetName != undefined && assetName != "")
    || (assetID != undefined && assetID != "")
    || (assetSerialNumber != undefined && assetSerialNumber != "")
    || (dnsHostName != undefined && dnsHostName != "")
    ) 
