/*
    lz121597 - Can also pull data for each Thing Template type separately
    (non Dynamic) if there is concern that pulling at BaseDevice TT level
    may mix up results across Thing Types
*/

var enablescriptdebug = false;

if (enablescriptdebug) {
    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:GetCountAssetsByModelForCust:Start");
}

var query =
    {
        "sorts": [
            {
                "fieldName": "Asset_Model",
                "isAscending": false,
                "isCaseSensitive": false
            },
            {
                "fieldName": "thingTemplate"
            }
        ],
        "filters": {
            "type": "AND",
            "filters": [
                {
                    "type": "EQ",
                    "fieldName": "Customer",
                    "value": customerName
                },
                {
                    "type": "NE",
                    "fieldName": "Asset_Model",
                    "value": ""
                }
            ]
        }
    };

if (isConnected == true) {
    query.filters.filters.push({
        "fieldName": "isConnected",
        "type": "EQ",
        "value": true
    });
}

var paramsREL = {
    maxItems: 500000 /* NUMBER */,
    nameMask: undefined /* STRING */,
    query: query /* QUERY */,
    tags: undefined /* TAGS */
};

// result: INFOTABLE dataShape: RootEntityList
var itREL = ThingTemplates["NCR.BaseDevice"].QueryImplementingThingsWithData(paramsREL);

/*
// Sort Results by Model - Moved Sort to query
var sort = new Object();
sort.name = "Asset_Model";
sort.ascending = false;
itREL.Sort(sort);
*/

var tableLength = itREL.rows.length;

if (enablescriptdebug) {
    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:GetCountAssetsByModelForCust:Devices Found:" + tableLength);
}

var params = {
    infoTableName: "InfoTable",
    dataShapeName: "NCR.DCS.AssetsByModelDS"
};

// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE (NCR.DCS.AssetsByModelDS)
var result = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape(params);

var total = 0;

for (var x = 0; x < tableLength; x++) {
    var row = itREL.rows[x];

	/* 
        Thing Template - Asset_Type
		NCR.NETPRINTER - PRINTER
		NCR.POSWM - POSWM
		NCR.HANDHELD - HANDHELD
		NCR.MGMTSERVER - MGMTSERVER
		NCR.ATM - ATM
		NCR.BOSERVER - BOSERVER
		NCR.SCO - SCO
		NCR.LAPTOP - LAPTOP
		NCR.POS  - POS
		HOBART.SCALE - SCALE
		NCR.NETWORKCAMERA - NETWORKCAMERA
	*/

    var sAssetTypeThingCurrent = "Unknown";
    var sAssetTypeThingPrevious = "Unknown";

    var sAssetTypeThingCurrent = "Unknown";
    if (row.thingTemplate == "NCR.NETPRINTER") { sAssetTypeThingCurrent = "PRINTER"; }
    if (row.thingTemplate == "NCR.POSWM") { sAssetTypeThingCurrent = "POSWM"; }
    if (row.thingTemplate == "NCR.HANDHELD") { sAssetTypeThingCurrent = "HANDHELD"; }
    if (row.thingTemplate == "NCR.MGMTSERVER") { sAssetTypeThingCurrent = "MGMTSERVER"; }
    if (row.thingTemplate == "NCR.ATM") { sAssetTypeThingCurrent = "ATM"; }
    if (row.thingTemplate == "NCR.BOSERVER") { sAssetTypeThingCurrent = "BOSERVER"; }
    if (row.thingTemplate == "NCR.SCO") { sAssetTypeThingCurrent = "SCO"; }
    if (row.thingTemplate == "NCR.LAPTOP") { sAssetTypeThingCurrent = "LAPTOP"; }
    if (row.thingTemplate == "NCR.POS") { sAssetTypeThingCurrent = "POS"; }
    if (row.thingTemplate == "HOBART.SCALE") { sAssetTypeThingCurrent = "SCALE"; }
    if (row.thingTemplate == "NCR.NETWORKCAMERA") { sAssetTypeThingCurrent = "NETWORKCAMERA"; }

    if (x != 0) {
        if (itREL.rows[x - 1].thingTemplate == "NCR.NETPRINTER") { sAssetTypeThingPrevious = "PRINTER"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.POSWM") { sAssetTypeThingPrevious = "POSWM"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.HANDHELD") { sAssetTypeThingPrevious = "HANDHELD"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.MGMTSERVER") { sAssetTypeThingPrevious = "MGMTSERVER"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.ATM") { sAssetTypeThingPrevious = "ATM"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.BOSERVER") { sAssetTypeThingPrevious = "BOSERVER"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.SCO") { sAssetTypeThingPrevious = "SCO"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.LAPTOP") { sAssetTypeThingPrevious = "LAPTOP"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.POS") { sAssetTypeThingPrevious = "POS"; }
        if (itREL.rows[x - 1].thingTemplate == "HOBART.SCALE") { sAssetTypeThingPrevious = "SCALE"; }
        if (itREL.rows[x - 1].thingTemplate == "NCR.NETWORKCAMERA") { sAssetTypeThingPrevious = "NETWORKCAMERA"; }
    }


    if (x == 0) {
        // first fow
        total = 1;
    } else if (x == (tableLength - 1)) {
        // Last Row
        if (row.Asset_Model.trim().toUpperCase() == itREL.rows[x - 1].Asset_Model.trim().toUpperCase()) {
            var rowObject = new Object();
            rowObject.assetModel = sAssetTypeThingCurrent + " - " + row.Asset_Model;
            rowObject.total = total + 1;
            result.AddRow(rowObject);
        } else {
            // Previous Row does not match this row ; need to process both previous and this row					
            var rowObject = new Object();
            rowObject.assetModel = sAssetTypeThingPrevious + " - " + itREL.rows[x - 1].Asset_Model;
            rowObject.total = total;
            result.AddRow(rowObject);

            // Current Row
             var rowObject = new Object();
            rowObject.assetModel = sAssetTypeThingCurrent + " - " + row.Asset_Model;
            rowObject.total = 1;
            result.AddRow(rowObject);
        }

    } else {
        if (row.Asset_Model.trim().toUpperCase() == itREL.rows[x - 1].Asset_Model.trim().toUpperCase()) {
            total = total + 1;
        } else {
            var rowObject = new Object();
            rowObject.assetModel = sAssetTypeThingPrevious + " - " + itREL.rows[x - 1].Asset_Model;
            rowObject.total = total;
            result.AddRow(rowObject);

            total = 1;
        }
    }

}

// Sort Results by Model
var sort = new Object();
sort.name = "assetModel";
sort.ascending = false;
result.Sort(sort);


