var result;
var msname;
var disableSWCM = false;

if (assetType.toUpperCase() == 'SCALE') {
    msname = 'NCR.AssetsKSMashup';
    disableSWCM = true;
} else if (assetType.toUpperCase() == 'POSWM') {
	msname = "NCR.AssetsPOSWMMashup";
	disableSWCM = true;
} else if (assetType.toUpperCase() == 'HANDHELD') {
	msname = "NCR.AssetsHandHeldMashup";
} else if (assetType.toUpperCase() == 'PRINTER') {
	msname = "NCR.AssetsSNMPMashup";
	disableSWCM = true;
} else {
	msname = "NCR.AssetsMashup";
}

if (!assetType || assetType == "") {
	msname = "NCR.AssetsDefaultMashup";
}

if (!assetName || assetName == '') {
    msname = "NCR.DCS.DefaultAssetManagementMashup";
}

if (menuSelected != null && menuSelected != "") {
	if (menuSelected != "NCR.DCS.DefaultAssetManagementMashup") {
		msname = menuSelected;
	}
} 

// Do not return mashup if click on Customer, Site or AssetType
if (entityState == "Customer" || entityState == "Site" || entityState == "AssetType") {
	if (menuSelected == "NCR.DCS.DefaultAssetManagementMashup") {
		msname = "NCR.DCS.DefaultAssetManagementMashup";
	}

}

// Return NCR.DCS.AssetHHWallMashup if Customer Ahold-Delhaize 
// and entityState is AssetType and assetType is HANDHELD
if (entityState == "AssetType" && customerName == "Ahold-Delhaize" 
	&& assetType == "HANDHELD" && menuSelected == "NCR.DCS.DefaultAssetManagementMashup") {
	msname = "NCR.DCS.AssetHHWallMashup";
}

