var paramsCS = {
    propertyName: "CS_ALL" /* STRING */
};

try {

    // result: STRING
    var invall = Things[assetName].GetStringPropertyValue(paramsCS);

    if (invall && invall != "") {
        if (invall.indexOf("[") != 0) {
            invall = "[" + invall + "]";
        }
    }

    if (invall == null || invall == '') {
        invall = '[{"Data Not Availble":"Data Not Availble"}]';
    }

    // Create JSON object from String
    var objJSON = JSON.parse(invall);

    var params = {
        minDateMilliseconds: undefined /* LONG */,
        // Long Numeric values get converted (ex. CS, Disk, Drive) dateFormat: "RAW" /* STRING */,
        dateFormat: undefined /* STRING */,
        json: objJSON /* JSON */,
        dataShape: undefined /* DATASHAPENAME */
    };

    // result: INFOTABLE using datShape specified as param
    result = Resources["Parsley"].ParseJSON(params).array;

} catch (e) {

    if (enablescriptdebug) {
        logger.debug("NCR.BaseDeviceHelper::GetPropertiesForComputerSystem::ERROR:" + e.toString());
    }
}

