var enablescriptdebug = false;

if (enablescriptdebug) {
	logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper::GetRemoteActionsForCategory" 
		+ ":assetName:" + assetName
		+ ":assetType:" + assetType
		+ ":category:" + category
		+ ":customer:" + customer
	);
}

// Get OS of Asset
var params_OS = {
	propertyName: "OS_ALL" /* STRING */
};

// Get OS to separate Windows and Linux Calls
// result: STRING
var os_all;
if (assetName && assetName != '') {
	os_all = Things[assetName].GetStringPropertyValue(params_OS);
} else {
	os_all = '';
}

if (os_all && os_all != "") {
	if (os_all.indexOf("[") != 0) {
		os_all = "[" + os_all + "]";
	}
}

if (os_all == null || os_all == '') {
	os_all = '[{"Caption":""}]';
}

// Create JSON object from String
var objJSON = JSON.parse(os_all);
var caption = objJSON[0].Caption;
var osName;

if (caption.toUpperCase().indexOf("WINDOWS") > -1) {
	osName = "Windows";
} else if (caption.toUpperCase().indexOf("LINUX") > -1) {
	osName = "Linux";
} else {
	osName = "None";
}

if (enablescriptdebug) {
	logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper::GetRemoteActionsForCategory:"	+ "osName:" + osName);
}

// Query NCR.DCS.RemoteActionsDT
var valuesRA = Things["NCR.DCS.RemoteActionsDT"].CreateValues();

/*values.autoaction = undefined; //STRING
values.role = undefined; //STRING
values.remoteactionid = undefined; //STRING [Primary Key]
values.allowedstate = undefined; //STRING
values.responsetype = undefined; //STRING
values.command = undefined; //STRING
values.allowedtimewindow = undefined; //STRING
values.parameter = undefined; //STRING
values.name = undefined; //STRING
values.assettype = undefined; //STRING
*/

if (category) { valuesRA.category = category; }
if (osName) { valuesRA.os = osName; }
if (customer) {	valuesRA.customer = customer; }

var params = {
	maxItems: undefined /* NUMBER */,
	values: valuesRA /* INFOTABLE */,
	query: undefined /* QUERY */,
	source: undefined /* STRING */,
	tags: undefined /* TAGS */
};

// result: INFOTABLE
var raIT = Things["NCR.DCS.RemoteActionsDT"].QueryDataTableEntries(params);

var paramsRA = {
	infoTableName: "InfoTable",
	dataShapeName: "NCR.DCS.RemoteActionCommandsDS"
};

// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(NCR.DCS.RemoteActionCommandsDS)
var result = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape(paramsRA);

// Parse through raIT InfoTable and generate rows
var tableLength = raIT.rows.length;
for (var y = 0; y < tableLength; y++) {
	var raC = raIT.rows[y];

	// Roles	
	var admin = false;
	var tcs = false;
	var servicedesk = false;


	// Restrict access to specific  groups
	// result: INFOTABLE dataShape: EntityList
	var uAG = Resources["CurrentSessionInfo"].GetCurrentUserGroups();

	var tableLengthUG = uAG.rows.length;
	for (var w = 0; w < tableLengthUG; w++) {
		if (uAG.rows[w].name == 'Administrators' || uAG.rows[w].name == 'NCR.DCS.DEVELOPERS') {
			admin = true;
		}
		if (uAG.rows[w].name == 'NCR.ICPSpecialistL2Group') {
			tcs = true;
		}
		if (uAG.rows[w].name == 'NCR.ServiceDesk') {
			servicedesk = true;
		}
		if (enablescriptdebug) {
			logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper::GetRemoteActionsForCategory:UserGroup:" + uAG.rows[w].name);
		}
	}	

	var roleAllows = false;
	if (admin == true) {
		roleAllows = true;
	} else if (raC.role.indexOf("TCS") > -1 && tcs == true) {
		roleAllows = true;
	} else if (raC.role.indexOf("ServiceDesk") > -1 && servicedesk == true) {
		roleAllows = true;
	}

	// If Remote Action is associated with Device Type
	if (raC.assettype.indexOf(assetType.toUpperCase()) > -1) {
		// If Remote Action is associated with User Group membership
		if (roleAllows == true) {
			// Add Commands
			var newEntry = new Object();
			newEntry.CommandName = raC.name;
			newEntry.Command = raC.remoteactionid;
			newEntry.Parameters = '';
			result.AddRow(newEntry);
		}		
	}
}


