// Call ExecuteCommand Service for Asset
var racResponse = "";

// Process Command for <%Asset_ProgramDir%> and <%Asset_DataDir%>
// Get Path from Property Asset_ProgramDir; Data Directory propertyName: "Asset_DataDir" /* STRING */ 
var paramsPD = {
    propertyName: "Asset_ProgramDir" /* STRING */
};
var apd = Things[assetName].GetStringPropertyValue(paramsPD);
var paramsDD = {
    propertyName: "Asset_DataDir" /* STRING */
};
var add = Things[assetName].GetStringPropertyValue(paramsDD);

var progPathDefault = "C:\\Program Files\\NCR\\Microserver";
var dataPathDefault = "C:\\ProgramData\\NCR\\Microserver";
var progPath;
var dataPath;
if (apd != null && apd != '') {
    progPath = apd.replace(/\\/g, '\\');
} else {
    progPath = progPathDefault.replace(/\\/g, '\\');
}
if (add != null && add != '') {
    dataPath = add.replace(/\\/g, '\\');
} else {
    dataPath = dataPathDefault.replace(/\\/g, '\\');
}

var commandP = command.replace(/<%Asset_ProgramDir%>/g, progPath);
var commandF = commandP.replace(/<%Asset_DataDir%>/g, dataPath);

var paramsEC = {
	command: commandF /* STRING */
};

// result: STRING
racResponse = Things[assetName].ExecuteCommand(paramsEC);

var result;

// Process result (JSON or Text)
if (responseType == "JSON") {

	var paramsHTML = {
		outputRA: racResponse /* STRING */
	};

	// result: HTML
	var result = Things["NCR.BaseDeviceHelper"].GetHTMLTableForRemoteActionsOutput(paramsHTML);

} else {
	result = racResponse;
}


