// Only process 1000 at a time
// if (tableLength > 1000) { tableLength = 1000;}

for (var x = 0; x < tableLength; x++) {
    var row = assetThings.rows[x];

    if (Things[row.name].thingTemplate == "NCR.LAPTOP" || Things[row.name].thingTemplate == "NCR.POS"
        || Things[row.name].thingTemplate == "NCR.ATM"
        || Things[row.name].thingTemplate == "NCR.SCO"
        || Things[row.name].thingTemplate == "NCR.MGMTSERVER"
        || Things[row.name].thingTemplate == "NCR.BOSERVER") {

        if (Things[row.name].CS_ALL && Things[row.name].CS_ALL != "") {
            try {
                if (enablescriptdebug) {
					/*
					logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetBaseDeviceProperties:Device:"
						+ row.name + ":Get Properties:"
						+ "Things[row.name].CS_ALL:" + Things[row.name].CS_ALL);	
					*/
                }
                var csall = Things[row.name].CS_ALL;

                if (Things[row.name].CS_ALL.indexOf("[") != 0) {
                    csall = "[" + Things[row.name].CS_ALL + "]";
                }

                var objJSONCS = JSON.parse(csall);

                if ((Things[row.name].Asset_Name == "" || Things[row.name].Asset_Name == "undefined" ) 
                    && objJSONCS[0].DNSHostName != undefined && objJSONCS[0].DNSHostName != "") {
                    Things[row.name].Asset_Name = objJSONCS[0].DNSHostName;

                    if (enablescriptdebug) {
                        logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetBaseDeviceProperties:Device:"
                            + row.name + ":Set Property"
                            + ":Asset_Name:" + objJSONCS[0].DNSHostName);
                    }
                }
                if ((Things[row.name].Asset_Model == "" || Things[row.name].Asset_Model == "undefined" ) 
                    && objJSONCS[0].Model != undefined && objJSONCS[0].Model != "") {
                    Things[row.name].Asset_Model = objJSONCS[0].Model;

                    if (enablescriptdebug) {
                        logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetBaseDeviceProperties:Device:"
                            + row.name + ":Set Property"
                            + ":Asset_Model:" + objJSONCS[0].Model);
                    }
                }
                if ((Things[row.name].Asset_Manufacturer == "" || Things[row.name].Asset_Manufacturer == "undefined") 
                    && objJSONCS[0].Manufacturer != undefined && objJSONCS[0].Manufacturer != "") {
                    Things[row.name].Asset_Manufacturer = objJSONCS[0].Manufacturer;

                    if (enablescriptdebug) {
                        logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetBaseDeviceProperties:Device:"
                            + row.name + ":Set Property"
                            + ":Asset_Manufacturer:" + objJSONCS[0].Manufacturer);
                    }
                }

            } catch (e) {
                logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetBaseDeviceProperties:Device:"
                    + row.name + ":Set Properties CS_ALL Failed:ERROR:" + e.toString());
            }
        }

        if (Things[row.name].OS_ALL && Things[row.name].OS_ALL != "") {
            try {

                if (enablescriptdebug) {
					/*
					logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetBaseDeviceProperties:Device:"
