			if (Things[row.name].thingTemplate == "NCR.POSWM") { Things[row.name].Asset_Type = "POSWM"; sAT = "POSWM";}
            if (Things[row.name].thingTemplate == "NCR.LAPTOP") { Things[row.name].Asset_Type = "LAPTOP"; sAT = "LAPTOP";}
            if (Things[row.name].thingTemplate == "NCR.MGMTSERVER") { Things[row.name].Asset_Type = "MGMTSERVER"; sAT = "MGMTSERVER";}

            if (enablescriptdebug) {

                logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustomerPropForWalmart:Set Asset_Type "
                    + sAT + " on Device:"
                    + row.name);

