var params = {
	maxItems: 100000 /* NUMBER */,
	nameMask: undefined /* STRING */,
    query: query /* QUERY */,
	tags: undefined /* TAGS */
};

// result: INFOTABLE dataShape: RootEntityList
var poswmThings = ThingTemplates["NCR.POSWM"].QueryImplementingThingsWithData(params);

var tableLength = poswmThings.rows.length;
if (enablescriptdebug) {
    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Number of Devices Found:" + tableLength);
}

for (var x = 0; x < tableLength; x++) {
    var row = poswmThings.rows[x];

    if (row.Customer == '') {
        if (enablescriptdebug) {
            logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Setting Customer Walmart on Device:"
                + row.name);
        }
        try {
            Things[row.name].Customer = "Walmart";
        } catch (e) {
            logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Set Customer Walmart on Device "
                + row.name + "Failed:ERROR:"
                + e.toString());
        }

    }

    // lz121597 Sprint 13 - Set Asset_Type
    if (row.Asset_Type == '' || row.Asset_Type == 'undefined' ) {
        if (enablescriptdebug) {
            logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Setting Asset_Type on Device:"
                + row.name);
        }
        try {
            Things[row.name].Asset_Type = "POSWM";
        } catch (e) {
            logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Set Asset_Type on Device "
                + row.name + "Failed:ERROR:"
                + e.toString());
        }
    }

    // lz121597 Sprint 13 - Set Asset_Name
    if (row.Asset_Name == '' || row.Asset_Name == 'undefined') {
        if (enablescriptdebug) {
            logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Setting Asset_Name on Device:"
                + row.name);
        }
        try {

            var dnP = Things[row.name].CS_ALL;
            if (Things[row.name].CS_ALL.indexOf("[") != 0) {
                dnP = "[" + Things[row.name].CS_ALL + "]";
            }

            // Create JSON object from String
            var objJSONnewValue = JSON.parse(dnP);

            // DeviceName ex. {"DeviceName": "RG003_S01598_US"}
            var deviceName = objJSONnewValue[0].DeviceName;

            Things[row.name].Asset_Name = deviceName;
        } catch (e) {
            logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Set Asset_Name on Device "
                + row.name + "Failed:ERROR:"
                + e.toString());
        }
    }


    if (row.Asset_StoreIdentifier == '') {

        try {

            // Create JSON object from String
            //var objJSONnewValue = JSON.parse("[" + Things[row.name].CS_ALL + "]");

            // DeviceName ex. {"DeviceName": "RG003_S01598_US"}
            //var deviceName = objJSONnewValue[0].DeviceName;
            var dnTn = row.name;

            // var sSN = deviceName.substring(deviceName.indexOf("_S0") + 3, deviceName.lastIndexOf("_"));    
            var sSN = dnTn.substring(dnTn.indexOf("_S0") + 3, dnTn.lastIndexOf("_"));

            Things[row.name].Asset_StoreIdentifier = sSN;

            if (enablescriptdebug) {
                logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Set Asset_StoreIdentifier "
                    + sSN + " on Device:"
                    + row.name);
            }

        } catch (e) {
            logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetCustSiteForPOSWM:Set Asset_StoreIdentifier on Device "
                + row.name + " Failed:ERROR:"
                + e.toString());
        }
    }

}

