	logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetModelForNCRNetprinter:Number of Devices Found:" + tableLength);
}
logger.info("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetModelForNCRNetprinter:Number of Devices Found:" + tableLength);

// Only process 1000 at a time
