	var row = printerThings.rows[x];

	if (Things[row.name].PRT_ALL && Things[row.name].PRT_ALL != "") {
		try {
			if (enablescriptdebug) {
				/*
				logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetModelForNCRNetprinter:Device:"
					+ row.name + ":Get Properties:"
					+ "Things[row.name].PRT_ALL:" + Things[row.name].PRT_ALL);
				*/
			}

			var prtall = Things[row.name].PRT_ALL;
			if (Things[row.name].PRT_ALL.indexOf("[") != 0) {
				prtall = "[" + Things[row.name].PRT_ALL + "]";
			}

			var objJSONPRT = JSON.parse(prtall);

			Things[row.name].Asset_Model = objJSONPRT[0].Model;

			if (enablescriptdebug) {
				logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetModelForNCRNetprinter:Device:"
					+ row.name + ":Set Property NETPRT"
					+ ":Asset_Model:" + objJSONPRT[0].Model);
			}		

		} catch (e) {
			logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetModelForNCRNetprinter:Device:"
				+ row.name + ":Set Properties OS_ALL HH Failed:ERROR:" + e.toString());
		}
	}
}

