    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteForNCRNetprinter:Start");
}

var query = {
	"filters": {
		"type": "AND",
		"filters": [
			{
				"fieldName": "Asset_StoreIdentifier",
				"type": "EQ",
				"value": ""
			}	
		]
	}
}

var params = {
	maxItems: 30000 /* NUMBER */,
	nameMask: undefined /* STRING */,
	query: query /* QUERY */,
	tags: undefined /* TAGS */
};

// result: INFOTABLE dataShape: RootEntityList
var netPrinterThings = ThingTemplates["NCR.NETPRINTER"].QueryImplementingThingsWithData(params);

// result: INFOTABLE dataShape: RootEntityList
// var netPrinterThings = ThingTemplates["NCR.NETPRINTER"].GetImplementingThingsWithData();

var tableLength = netPrinterThings.rows.length;

if (enablescriptdebug) {
    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteForNCRNetprinter:Number of Devices Found:" + tableLength);
}

if (tableLength > 1000) {
    tableLength = 1000;
}

for (var x = 0; x < tableLength; x++) {
    var row = netPrinterThings.rows[x];   

    if (row.Asset_StoreIdentifier == '') {

        try {

            // Create JSON object from String
            //var objJSONnewValue = JSON.parse("[" + Things[row.name].CS_ALL + "]");

            // DeviceName ex. {"DeviceName": "RG003_S01598_US"}
            //var deviceName = objJSONnewValue[0].DeviceName;
            var dnTn = row.name;

            // var sSN = deviceName.substring(deviceName.indexOf("_S0") + 3, deviceName.lastIndexOf("_"));    
            var sSN = dnTn.substring(dnTn.indexOf("_s0") + 3, dnTn.indexOf("_us_wal_mart_com"));

            Things[row.name].Asset_StoreIdentifier = sSN;

            if (enablescriptdebug) {
                logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteForNCRNetprinter:Set Asset_StoreIdentifier "
                    + sSN + " on Device:"
                    + row.name);
            }

        } catch (e) {
            logger.warn("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteForNCRNetprinter:Set Asset_StoreIdentifier on Device "
                + row.name + " Failed:ERROR:"
                + e.toString());
        }
    }

}

var result = "Completed - See Script Log for result";


var enablescriptdebug = false;

if (enablescriptdebug) {
    logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Start");
}
logger.info("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Start");

// Only for Customer Walmart and only for NCR.LAPTOP and only for Devices with Asset_StoreIdentifier not set
// And device isConnected is true
var query = {
	"filters": {
		"type": "AND",
		"filters": [
			{
				"fieldName": "Customer",
				"type": "EQ",
				"value": "Walmart"
			},	
			{
				"fieldName": "Asset_StoreIdentifier",
				"type": "EQ",
				"value": ""
			},
			{
				"fieldName": "isConnected",
				"type": "EQ",
				"value": "true"
			}	
		]
	}
}

var params = {
	maxItems: 30000 /* NUMBER */,
	nameMask: undefined /* STRING */,
	query: query /* QUERY */,
	tags: undefined /* TAGS */
};

// result: INFOTABLE dataShape: RootEntityList
var lapWmThings = ThingTemplates["NCR.LAPTOP"].QueryImplementingThingsWithData(params);

var tableLength = lapWmThings.rows.length;
if (enablescriptdebug) {
	logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Number of Devices Found:" + tableLength);
}
logger.info("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Number of Devices Found:" + tableLength);

// Only Process 100
if (tableLength > 10) { tableLength = 10;}

for (var x = 0; x < tableLength; x++) {
	var row = lapWmThings.rows[x];

	try {
		// Get Site Name by Executing Command 
		// REG QUERY HKLM\System\CurrentControlSet\Services\tcpip\parameters /v DHCPDomain
		// result: _HKEY_LOCAL_MACHINE\System\CurrentControlSet\Services\tcpip\parameters_    DHCPDomain    REG_SZ    s01598.us.wal-mart.com__

		var paramsEC = {
			command: "REG QUERY HKLM\\System\\CurrentControlSet\\Services\\tcpip\\parameters /v DHCPDomain" /* STRING */
		};

		// result: STRING
		var ecData = Things[row.name].ExecuteCommand(paramsEC);

		if (enablescriptdebug) {
			logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:ExecuteCommand on Device:"
				+ row.name + ":result:" + ecData);
		}

		// var sSN = deviceName.substring(deviceName.indexOf("_S0") + 3, deviceName.lastIndexOf("_"));    
		var sSN = ecData.substring(ecData.indexOf("REG_SZ    s0") + 12, ecData.lastIndexOf(".us.wal-mart.com"));

		Things[row.name].Asset_StoreIdentifier = sSN;

		if (enablescriptdebug) {
			logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Set Asset_StoreIdentifier:"
				+ sSN + ":on Device:"
				+ row.name);
		}

	} catch (e) {
		if (enablescriptdebug) {
        	logger.debug("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Set Asset_StoreIdentifier on Device "
				+ row.name + " Failed:ERROR:"
				+ e.toString());
        }
	}

}

logger.info("NCRDCSMASHUP::NCR.BaseDeviceHelper:SetSiteFromRegForWalmartLaptops:Completed");

var result = "Completed - See Script Log for result";


var enablescriptdebug = false;

if (enablescriptdebug) {
	logger.debug("NCR.BaseDeviceHelper::ShowFilesTabAssetMasup::assetType:" + assetType);
}

var result = false;

if (assetType == undefined || assetType == "") {
	result = false;
} else {

	try {

		var deviceSupported = false;

		if (assetType.toUpperCase() == "ATM") {
			deviceSupported = true;
		}
		if (assetType.toUpperCase() == "POS") {
			deviceSupported = true;
		}
		if (assetType.toUpperCase() == "LAPTOP") {
			deviceSupported = true;
		}
		if (assetType.toUpperCase() == "HANDHELD") {
			deviceSupported = true;
		}
		if (assetType.toUpperCase() == "MGMTSERVER") {
			deviceSupported = true;
		}

		// Restrict access to specific  groups
		// result: INFOTABLE dataShape: EntityList
		var uAG = Resources["CurrentSessionInfo"].GetCurrentUserGroups();

		// TCS L2 Group
		var l2 = false;

		// Service Desk - Should not have access to Open Files
		var sd = false;

		var tableLength = uAG.rows.length;
		for (var y = 0; y < tableLength; y++) {
			if (deviceSupported == true) {
				if (uAG.rows[y].name == 'NCR.ICPSpecialistL2Group') {
					l2 = true;
				}
				if (uAG.rows[y].name == 'NCR.ServiceDesk') {
					sd = true;
				}
			}
		}

		if (l2 == true && sd == false) {
			result = true;
		}
	} catch (e) {
		if (enablescriptdebug) {
			logger.debug("NCR.BaseDeviceHelper::ShowFilesTabAssetMasup::ERROR:" + e.toString());
		}
	}

}

if (enablescriptdebug) {
	logger.debug("NCR.BaseDeviceHelper::ShowFilesTabAssetMasup::assetType:" + assetType + ":result:" + result);
}


var result = false;
var deviceSupported = false;
if(assetType == "ATM"){
	deviceSupported = true;
}
if(assetType == "POS"){
	deviceSupported = true;
}
if (assetType == "LAPTOP") {
	deviceSupported = true;
}
if (assetType == "MGMTSERVER") {
	deviceSupported = true;
} 

// Restrict access to specific  groups
// result: INFOTABLE dataShape: EntityList
var uAG = Resources["CurrentSessionInfo"].GetCurrentUserGroups();

var tableLength = uAG.rows.length;
for (var y = 0; y < tableLength; y++) {
	if (deviceSupported == true) {
		if (uAG.rows[y].name == 'NCR.ICPSpecialistL2Group') {
				result = true;
		}
	}	
}


