var result = 0;
var initialTiles = me.GetInitialTileData();
var tableLength = initialTiles.rows.length;
var missingRecords = me.CreateValues();
for (var x = 0; x < tableLength; x++) {
    var row = initialTiles.rows[x];
    try {
        var params = {
            key: row.key /* STRING */
        };
        me.GetDataTableEntryByKey(params);
    } catch(err) {
        missingRecords.AddRow(row);
        result++;
    }
}
if (missingRecords.getRowCount() >= 1) {
    var params = {
        values: missingRecords
    };
    me.AddDataTableEntries(params);
}


