// when importing an old release, the data in this data table will be imported as well. We need to update some entries
var tiles = me.GetDataTableEntries();
var updates = me.CreateValues();
for (var i = 0, l = tiles.rows.length; i < l; i++) {
    var row = tiles.rows[i];

    // remove old RAC tile
    if (row.TileMashupOrURL === "TW.RSM.Mashup.AssetSearchByKeyword") {
        var values = me.CreateValues();
        values.AddRow(row);
        params = {
            values: values
        };
        me.DeleteDataTableEntries(params);
        continue;
    }

    // Workflow Editor updates
    if (row.key === "10") {
        row.TileMashupOrURL = "PTC.BusinessProcessEditor";
        row.TileLabel = "[[TW.UTL.WB.ApplicationTitle]]";
        row.TileTooltip = "[[PTC.Core.Create and edit workflows]]";
        row.TileBackground = "TW.UTL.TileBackground.Orange";
        row.TileIcon = "TW.UTL.TileIcon.WorkflowBuilder";
        row.IsMashup = true;
        row.LabelColor = "black";
        updates.AddRow(row);
    }

    // Workflow Manager updates
    if (row.key === "20") {
        row.TileMashupOrURL = "PTC.BusinessProcessManager";
        row.TileLabel = "[[TW.UTL.WM.ApplicationTitle]]";
        row.TileTooltip = "[[PTC.Core.View and manage workflows]]";
        row.TileBackground = "TW.UTL.TileBackground.Orange";
        row.TileIcon = "TW.UTL.TileIcon.WorkflowMgt";
        row.IsMashup = true;
        row.LabelColor = "black";
        updates.AddRow(row);
    }

    // Asset Manager updates
    if (row.key === "45") {
        row.TileMashupOrURL = "PTC.Asset.Mashup.AssetSearchByKeyword";
        row.TileLabel = "[[TW.UTL.Asset.ApplicationTitle]]";
        row.TileTooltip = "[[PTC.Core.AssetMonitorTileTooltip]]";
        row.TileBackground = "TW.UTL.TileBackground.LightBlue";
        row.TileIcon = "TW.UTL.TileIcon.AssetMgt";
        row.IsMashup = true;
        row.LabelColor = "black";
        updates.AddRow(row);
    }

    // SCM updates
    if (row.key === "50") {
        row.TileMashupOrURL = "TW.RSM.SFW.Mashup.SoftwareDashboard";
        row.TileLabel = "[[TW.RSM.SFW.ApplicationTitle]]";
        row.TileTooltip = "[[PTC.Core.Create, manage, test, and deploy packages to connected assets]]";
        row.TileBackground = "TW.UTL.TileBackground.DarkBlue";
        row.TileIcon = "TW.UTL.TileIcon.SoftwareContentMgt";
        row.IsMashup = true;
        row.LabelColor = "black";
        if (!(row.RestrictedUserGroups || "").trim()) {
            row.RestrictedUserGroups = "TW.RSM.RemoteServices, TW.RSM.Admin";
        }
        updates.AddRow(row);
    }

    // Alert Manager updates
    if (row.key === "55") {
        row.TileMashupOrURL = "PTC.Alert.Mashup.AlertMonitor";
        row.TileLabel = "[[TW.UTL.Alert.ApplicationTitle]]";
        row.TileTooltip = "[[PTC.Alert.AlertMonitorTileTooltip]]";
        row.TileBackground = "TW.UTL.TileBackground.Red";
        row.TileIcon = "TW.UTL.TileIcon.AlertMgt";
        row.IsMashup = true;
        row.LabelColor = "black";
        updates.AddRow(row);
    }

    // PRM updates
    if (row.key === "30") {
        row.TileMashupOrURL = "PTC.CRM.ProductRelationshipManager";
        row.TileLabel = "[[TW.UTL.PRM.ApplicationTitle]]";
        row.TileTooltip = "[[PTC.Core.Manage relationships between customers and assets]]";
        row.TileBackground = "TW.UTL.TileBackground.Green";
        row.TileIcon = "TW.UTL.TileIcon.ProductRelationshipMgt";
        row.IsMashup = true;
        row.LabelColor = "black";
        updates.AddRow(row);
    }

    // help center updates
    if (row.key === "70") {
        row.TileMashupOrURL = "[[TW.UTL.HelpCenterURL.8]]";
        row.TileLabel = "[[PTC.Core.ThingWorx Utilities Help Center]]";
        row.TileTooltip = "[[PTC.Core.ThingWorx Utilities Help Center]]";
        row.TileBackground = "TW.UTL.TileBackground.Purple";
        row.TileIcon = "TW.UTL.TileIcon.HelpCenter";
        row.IsMashup = false;
        row.LabelColor = "black";
        updates.AddRow(row);
    }

    // composer updates
    if (row.key === "60") {
        row.TileMashupOrURL = "/Thingworx/Composer/";
        row.TileLabel = "[[PTC.Core.ThingWorx Composer]]";
        row.TileTooltip = "[[PTC.Core.Build and model unique applications]]";
        row.TileBackground = "TW.UTL.TileBackground.DarkGreen";
        row.TileIcon = "TW.UTL.TileIcon.TwxComposer";
        row.IsMashup = false;
        row.LabelColor = "black";
        updates.AddRow(row);
    }
    // audit management
    if (row.key === "65") {
        row.TileMashupOrURL = "TW.UTL.Audit.Mashup.AuditArchive";
        row.TileLabel = "Audit Management";
        row.TileTooltip = "Audit Management";
        row.TileBackground = "TW.UTL.TileBackground.Red";
        row.TileIcon = "TW.UTL.TileIcon.AuditMgt";
        row.IsMashup = true;
        row.LabelColor = "black";
        updates.AddRow(row);
    }
}
